import React, { useState } from 'react';

function App(props) {
    const [status, setstatus] = useState(0);
    console.log(status);
    const Submit = (det) => {
        fetch("http://127.0.0.1:3000/values", {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            details: det,
          }),
        })
          .then((res) => res.json())
          .then(async (value) => {
            setstatus(value.sta);
          });
      };
    return (
        <div>
            <form>
                <a>Enter the name:</a>
                <input type="text" name='name'></input>
                <button type="submit" onClick={Submit}>Submit</button>
            </form>
        </div>
    );
}

export default App;
