import React, { useState } from "react";

function App() {
  const [status, setstatus] = useState(0);

  const Submit = (det) => {
    fetch("http://192.168.43.117:5000/", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        details: det,
      }),
    })
      .then((res) => res.json())
      .then(async (value) => {
        setstatus(value.sta);
      });
  };

  return (
    <div>
            <form>
                <a>Enter the name:</a>
                <input type="text" name={(name) => setstatus(name)}></input>
                <button type="submit" onClick={Submit}>Submit</button>
            </form>
        </div>
  );
}


export default App;
